INSERT INTO `mod` VALUES ('3', 'a:11:{i:0;a:6:{s:3:\"aid\";s:1:\"1\";s:5:\"title\";s:61:\"台当局拒绝两岸联手护渔 枪击事件或还会发生\";s:4:\"size\";s:4:\"18px\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:1:\"1\";s:3:\"url\";s:11:\"/article/11\";}i:1;a:5:{s:3:\"aid\";s:1:\"2\";s:5:\"title\";s:57:\"本刊编辑部：让合作的阳光驱散疫情的阴霾\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}i:2;a:5:{s:3:\"aid\";s:1:\"3\";s:5:\"title\";s:51:\"万字长文！习近平这样谈抗疫国际合作\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}i:3;a:5:{s:3:\"aid\";s:1:\"4\";s:5:\"title\";s:54:\"习近平谈国际社会战胜疫情的最有力武器\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}i:4;a:5:{s:3:\"aid\";s:1:\"5\";s:5:\"title\";s:42:\"八个视角，读懂总书记陕西之行\";s:4:\"size\";s:4:\"18px\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:1:\"1\";}i:5;a:5:{s:3:\"aid\";s:1:\"6\";s:5:\"title\";s:54:\"摆脱贫困，总书记一直在思考的重大问题\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}i:6;a:5:{s:3:\"aid\";s:1:\"7\";s:5:\"title\";s:57:\"从七个“战贫”瞬间读懂习近平的为民初心\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}i:7;a:5:{s:3:\"aid\";s:1:\"8\";s:5:\"title\";s:46:\"重温习近平殷殷嘱托 共建美丽家园\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}i:8;a:5:{s:3:\"aid\";s:1:\"9\";s:5:\"title\";s:51:\"应对突发事件是组织力建设的重大课题\";s:4:\"size\";s:4:\"18px\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:1:\"1\";}i:9;a:5:{s:3:\"aid\";s:2:\"10\";s:5:\"title\";s:51:\"抗疫展现中国高效动员力组织力协调力\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}i:10;a:5:{s:3:\"aid\";s:2:\"11\";s:5:\"title\";s:45:\"战“疫”，求是网系列评论来啦！\";s:4:\"size\";s:0:\"\";s:5:\"color\";s:0:\"\";s:4:\"bold\";s:0:\"\";}}');
